import sys
from PyQt5.QtWidgets import QApplication, QWidget, QLabel, QVBoxLayout, QProgressBar, QMessageBox
from PyQt5.QtGui import QIcon, QPixmap, QImage
from PyQt5.QtCore import QTimer
import base64
import random

class LoadingSimulator(QWidget):
    def __init__(self):
        super().__init__()

        self.init_ui()

    def init_ui(self):
        self.setWindowTitle('OScanner')
        self.setFixedSize(600, 150)

        # Устанавливаем иконку приложения
        icon_base64 = """"""
        icon_data = base64.b64decode(icon_base64)
        icon = QIcon(QPixmap.fromImage(QImage.fromData(icon_data)))
        self.setWindowIcon(icon)

        self.progress_bar = QProgressBar(self)
        self.label_status = QLabel('Инициализация сканера...', self)
        self.label_progress = QLabel('', self)

        self.layout = QVBoxLayout(self)
        self.layout.addWidget(self.label_status)
        self.layout.addWidget(self.progress_bar)
        self.layout.addWidget(self.label_progress)
        self.setLayout(self.layout)

        self.timer = QTimer(self)
        self.timer.timeout.connect(self.update_progress)

        self.scanning_time = 3600
        self.progress_value = 0

        self.show()
        self.timer.start(1000)

    def update_progress(self):
        if self.progress_value <= self.scanning_time:
            self.progress_value += 1
            progress_percent = (self.progress_value / self.scanning_time) * 100
            self.progress_bar.setValue(round(progress_percent))

            self.handle_scanning_stage()

        else:
            self.timer.stop()
            self.label_status.setText('Сканирование завершено!')
            self.label_progress.setText('Прогресс: 100%')
            self.show_report()

    def handle_scanning_stage(self):
        stages = {
            0.1: ('Инициализация сканера...', 'Подготовка к сканированию...'),
            0.2: ('Сканирование на наличие угроз...', 'Сканирование файлов...'),
            0.3: ('Анализ данных...', 'Анализ результатов сканирования...'),
            0.4: ('Обновление базы данных...', 'Проверка наличия обновлений...'),
            0.5: ('Поиск неизвестных угроз...', 'Глубокий анализ данных...'),
            0.6: ('Оптимизация процесса сканирования...', 'Повышение эффективности алгоритмов...'),
            0.7: ('Сверка результатов...', 'Проверка обнаруженных угроз...'),
            0.8: ('Резервное копирование данных...', 'Создание копии важных файлов...'),
            0.9: ('Оценка рисков...', 'Определение степени угроз для системы...')
        }

        for threshold, (status, progress) in stages.items():
            if self.progress_value < threshold * self.scanning_time:
                self.label_status.setText(status)
                self.label_progress.setText(progress)
                break

    def show_report(self):
        threats_detected = random.randint(1, 9)
        db_updates = random.randint(1, 9)

        report_text = f"""
        Сканирование завершено успешно!

        Результаты сканирования:
        - Обнаружено угроз: {threats_detected}
        - Обновлений базы данных: {db_updates}
        - Резервное копирование данных завершено.

        Спасибо за использование OScanner!
        """
        QMessageBox.information(self, 'Отчет по сканированию', report_text)

if __name__ == '__main__':
    app = QApplication(sys.argv)
    window = LoadingSimulator()
    sys.exit(app.exec_())
